var url = require("url");

var util = {};

util.clone = function(obj){
    return obj.constructor(obj.valueOf());
}

var date_time = function(){
    var date = new Date().toString().split(" ");
    return date[3] + "-" + date[2] + " " + date[4];
}

module.exports = {
    logger: {

        debug: function(message){
            console.debug(date_time() + " [DEBUG] - " + (message || "undefined message"));
        },

        log: function(message){
            console.log(date_time() + " [LOG] - " + (message || "undefined message"));
        },

        info: function(message){
            console.info(date_time() + " [INFO] - " + (message || "undefined message"));
        },

        warn: function(message){
            console.warn(date_time() + " [WARN] - " + (message || "undefined message"));
        },

        error: function(message){
            console.error(date_time() + " [ERROR] - " + (message || "undefined message"));
        }
    }
};
